<script>
export default {
    inject: [
        'openPopUpMenu',
        'openAuthPopUp'
    ],
    mounted() {
        if (this.$route.query.justLoggedIn) {
            this.openPopUpMenu('person');
        }
        if (this.$route.query.justRegistrated) {
            this.openAuthPopUp();
        }
    },
    watch: {
        $route(newRoute) {
            if (newRoute.query.justLoggedIn) {
                this.openPopUpMenu('person');
            }
            if (newRoute.query.justRegistrated) {
                this.openAuthPopUp();
            }
        }
    }
}
</script>
